function xyz_image = inv_ipt(ipt_image)	% Written by: Lawrence Taplin and Garrett M. Johnson%% This function inverts the IPT transform for display% % It uses a single number (0.43) for the inversion rather% than a spatially localized low-pass mask.%	imSize = size(ipt_image)	imSize = size(ipt_image);	xDim = imSize(2);	yDim = imSize(1);				inv_iptMat = inv([  [ 0.4000, 0.4000, 0.2000];...			[ 4.4550,-4.8510, 0.3960];...			[ 0.8056, 0.3572,-1.1628]]');				lms2xyz = cmatrix('lms2xyz');			lms_image = changeColorSpace( ipt_image, inv_iptMat );			xyz_image = changeColorSpace(abs(lms_image).^(1/.43), lms2xyz);